# deployment
DSO_ADDRESS = 'tcp://127.0.0.1:10001'
DATA_PATH = "data/"
TRANSACTION_GAS = "0x100000000"
BYTECODE = ""

SOLVING_INTERVAL = 10 # seconds (how often MatchingSolverWrapper solves the energy trading problem) 
INTERVAL_LENGTH = 60 # seconds (how often the components move onto the next time interval)
PREDICTION_WINDOW = 10 # how many time intervals ahead SmartHomeTraderWrapper trade (i.e., an offer is posted if offer['start'] <= time_interval + PREDICTION_WINDOW)
START_INTERVAL = 28 # starting time interval for the components


# model
FEEDERS = []
PROSUMERS = []
PROSUMER_FEEDER = {}
for (feeder, num_prosumers) in [(1, 9), (2, 17), (3, 5), (4, 13), (5, 8), (6, 1), (7, 10), (8, 17), (9, 5), (10, 13), (11, 4)]:
  FEEDERS.append(feeder)
  for prosumer in range(1, num_prosumers + 1):
    prosumer_id = feeder * 100 + prosumer
    PROSUMERS.append(prosumer_id)
    PROSUMER_FEEDER[prosumer_id] = feeder

from Microgrid import Microgrid
MICROGRID = Microgrid(
  interval_length=1.0, 
  C_ext=20000, 
  C_int=25000, 
  feeders=FEEDERS, 
  prosumer_feeder=PROSUMER_FEEDER)
  

